							  <div class=\"div-link\">\r\n									  <br><br>\r\n									  <a data-action=\"page\" href=\"http://www.yadingji.com.vn/lien-he\"><span class=\"fa moto-widget-theme-icon\"></span> <span class=\"moto-widget-button-label\">ĐẶT GIAO NGAY</span></a>\r\n								  </div>								  \r\n							  </div>\r\n							  <div class=\"row\">\r\n							  	  <div class=\"\">\r\n									  <div class=\"\" style=\"height:20px\"></div>\r\n								  </div>              \r\n							  </div>\r\n						  </div>						                                                                          \r\n					   </div>	  \r\n                	</div>\r\n           	   	</div>\r\n			</div>\';	    \r\n	}\r\n}\r\n\r\nadd_action( \'homepage\', \'storefront_order_online\', 85 );', '', 0, 1),
(5, 'Add special post to home page', 'Add special post to home page', 'if ( ! function_exists( \'storefront_add_special_post\' ) ) {\r\n   function storefront_add_special_post() {\r\n	  $args = array(\r\n		\'name\'        => \'special-post\',\r\n		\'post_type\'   => \'post\',\r\n		\'post_status\' => \'publish\',\r\n		\'numberposts\' => 1\r\n	  );\r\n	  $my_posts = get_posts($args);\r\n	  echo \'<div class=\"special-post\">\';\r\n	  if( $my_posts ) :	    \r\n	    $featureImages = wp_get_attachment_image_src( get_post_thumbnail_id($my_posts[0]->ID), \'full\', false, false);\r\n	    $url = \"url(\'\" . $featureImages[0] . \"\')\";\r\n	    echo \"<div class=\'feature-image col-6\'>\";\r\n	    echo \'<img src=\"\' . $featureImages[0] . \'\" alt=\"\" title=\"\" />\';\r\n	    echo \'</div>\';\r\n	    echo \'<div class=\"post-container col-6\">\';	  \r\n	 	echo \'<div class=\"post-title\">\' . $my_posts[0]->post_title . \'</div>\';\r\n	    echo \'<div class=\"post-divider-line\"><hr></div>\';\r\n		echo \'<div class=\"post-content\">\' . $my_posts[0]->post_content . \'</div>\';\r\n	    echo \'<div class=\"post-buttons\">\';\r\n	 	echo \'<a href=\"http://localhost/wordpress/the-nao-la-mot-mon-vit-quay-dung-chuan-hong-kong\"><span>XEM THÊM</span></a>\';	\r\n	    echo \'</div>\';	 \r\n	    echo \'</div>\';\r\n	  endif;	  \r\n	  echo \'</div>\';\r\n   }\r\n}  \r\n\r\nadd_action( \'homepage\', \'storefront_add_special_post\', 88 );\r\n\r\n', '', 0, 1),
(6, 'Add special images to top homepage', 'Add special images to top homepage', 'if ( ! function_exists( \'storefront_add_special_images\' ) ) {\r\n   function storefront_add_special_images() {\r\n	  $args = array(\r\n		\'post_name__in\'        => [ \'set-roll-1-pax\', \'happy-friday\', \'menu-combo-220k\' ],\r\n		\'post_type\'   => \'post\',\r\n		\'post_status\' => \'publish\'\r\n	  );\r\n	  $my_posts = get_posts($args);	  	\r\n	  echo \'<div class=\"special-images\">\';\r\n	  echo \'<div class=\"container-fluid\">\';	\r\n	  echo \'<div class=\"row\">\';\r\n	  if( $my_posts ) :	 		\r\n	      $length = count($my_posts);\r\n	 	  for($i = 0; $i < $length; $i++) {\r\n			  $featureImages = wp_get_attachment_image_src( get_post_thumbnail_id($my_posts[$i]->ID), \'full\', false, false);\r\n			  echo \'<div class=\"col\">\';\r\n			  echo \'<img src=\"\' . $featureImages[0] . \'\" alt=\"\" title=\"\" />\';\r\n			  echo \'</div>\';	\r\n		  } 	\r\n	  endif;\r\n	  echo \'</div>\';	  \r\n	  echo \'</div>\';\r\n	  echo \'</div>\';	 \r\n   }\r\n}\r\n\r\nif ( ! function_exists( \'storefront_add_promote_banners\' ) ) {\r\n   function storefront_add_promote_banners() {\r\n	  	echo \'<div class=\"promote-banners\">\';\r\n	    echo do_shortcode(\'[product_slider id=\"210\"]\');	    \r\n	    echo \'</div>\';\r\n   }\r\n}\r\n\r\nadd_action( \'homepage\', \'storefront_add_promote_banners\', 15 );', '', 0, 1),
(9, 'Add product categories to Shop page', 'Add product categories to Shop page', 'echo \'Add product categories to Shop page\';\r\nadd_action( \'widgets_init\', \'wb_extra_widgets\' );\r\n/**\r\n * Register new Widget area for Product Cat sort dropdown.\r\n */\r\nfunction wb_extra_widgets() {\r\n	register_sidebar( array(\r\n		\'id\'            => \'prod_sort\',\r\n		\'name\'          => __( \'Product Cat Sort\', \'themename\' ),\r\n		\'description\'   => __( \'This site below Shop Title\', \'themename\' ),\r\n		\'before_widget\'	=> \'<div>\',\r\n		\'after_widget\'	=> \'</div>\',\r\n		\'before_title\'  => \'<h3 class=\"widgettitle\">\',\r\n		\'after_title\'   => \'</h3>\'\r\n	) );\r\n}\r\nadd_action( \'woocommerce_before_shop_loop\',\'wb_prod_sort\' ); // Hook it after headline and before loop\r\n/**\r\n * Position the Product Category Sort dropdown.\r\n */\r\nfunction wb_prod_sort() {\r\n	if ( is_active_sidebar( \'prod_sort\' ) ) {\r\n		dynamic_sidebar( \'prod_sort\' );\r\n	}\r\n}', '', 0, 1);
